package fundamentos;

public class Inferencia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		double a = 4.5;
		
		System.out.println(a);
		
		var b = 4.5;
		
		System.out.println(b);//O JAVA ENTENDE É UM DOUBLE
		
		var c = "Texto"; // JAVA ENTENDE QUE É UMA STRING
		c = "outro texto";
		System.out.println(c);
		System.out.println(c);
	}

}
